package loop;

import java.util.Scanner;

public class Odd {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i,n=50;
				for (i = 25; i <= n; i++) {
			   if (i % 2 != 0) {
				System.out.print(i + " ");
			   }
			}
				System.out.print("These are odd numbers from 25 to 50"); 

	}

}
